import React, { useState } from 'react';
import './App.css';

const App = () => {
  const [username, setUsername] = useState('');
  const [userData, setUserData] = useState(null);
  const [error, setError] = useState('');
  const [darkMode, setDarkMode] = useState(true);

  const handleSearch = async () => {
    if (!username) return;
    setError('');
    setUserData(null);

    try {
      const res = await fetch(`https://api.github.com/users/${username}`);
      if (!res.ok) throw new Error('User not found');
      const data = await res.json();
      setUserData(data);
    } catch (err) {
      setError(err.message);
    }
  };

  return (
    <div className={`app ${darkMode ? 'dark-mode' : 'light-mode'}`}>
      <div className="container py-5">
        <div className="d-flex justify-content-between align-items-center mb-4">
          <div className="d-flex align-items-center">
            <img
              src="https://github.githubassets.com/images/modules/logos_page/GitHub-Mark.png"
              alt="GitHub"
              className="logo"
            />
            <h4 className="ms-2 fw-bold">GitHub Finder</h4>
          </div>
          <button onClick={() => setDarkMode(!darkMode)} className="btn btn-outline-light">
            {darkMode ? 'ðŸŒž Light' : 'ðŸŒ™ Dark'}
          </button>
        </div>

        <div className="input-group mb-4">
          <input
            type="text"
            className="form-control"
            placeholder="Enter GitHub username..."
            value={username}
            onChange={(e) => setUsername(e.target.value)}
          />
          <button onClick={handleSearch} className="btn btn-primary">
            Search
          </button>
        </div>

        {error && <div className="alert alert-danger text-center">{error}</div>}

        {userData && (
          <div className="card text-center p-4 shadow-lg profile-card">
            <img
              src={userData.avatar_url}
              alt="Avatar"
              className="rounded-circle avatar mx-auto"
            />
            <h4 className="mt-3">{userData.name || userData.login}</h4>
            <p className="text-muted">@{userData.login}</p>
            <p>{userData.bio}</p>
            <div className="d-flex justify-content-around my-3 fw-bold">
              <span>Repos: {userData.public_repos}</span>
              <span>Followers: {userData.followers}</span>
              <span>Following: {userData.following}</span>
            </div>
            <a
              href={userData.html_url}
              target="_blank"
              rel="noreferrer"
              className="btn btn-outline-info"
            >
              View Profile
            </a>
          </div>
        )}
      </div>
    </div>
  );
};

export default App;
