import React, { useState } from 'react';
import './App.css';
import { FaGithub, FaSun, FaMoon } from 'react-icons/fa';

function App() {
  const [username, setUsername] = useState('');
  const [userData, setUserData] = useState(null);
  const [error, setError] = useState(null);
  const [darkMode, setDarkMode] = useState(false);

  const fetchGitHubUser = async () => {
    try {
      const response = await fetch(`https://api.github.com/users/${username}`);
      if (!response.ok) throw new Error('User not found');
      const data = await response.json();
      setUserData(data);
      setError(null);
    } catch (err) {
      setUserData(null);
      setError(err.message);
    }
  };

  return (
    <div className={darkMode ? 'app dark' : 'app'}>
      <header className="app-header">
        <div className="branding">
          <img
            src="https://cdn-icons-png.flaticon.com/512/25/25231.png"
            alt="GitHub Logo"
            className="logo-img"
          />
          <h1>GitHub Profile Finder</h1>
        </div>
        <button className="theme-toggle" onClick={() => setDarkMode(!darkMode)}>
          {darkMode ? <FaSun /> : <FaMoon />}
        </button>
      </header>

      <div className="search-box">
        <input
          type="text"
          placeholder="Enter GitHub username..."
          value={username}
          onChange={(e) => setUsername(e.target.value)}
          onKeyDown={(e) => e.key === 'Enter' && fetchGitHubUser()}
        />
        <button onClick={fetchGitHubUser}>Search</button>
      </div>

      {error && <p className="error">{error}</p>}

      {userData && (
        <div className="card">
          <img src={userData.avatar_url} alt="avatar" className="avatar" />
          <h2>{userData.name || 'No name provided'}</h2>
          <p className="username">@{userData.login}</p>
          <p className="bio">{userData.bio || 'No bio available'}</p>
          <div className="stats">
            <span>Followers: {userData.followers}</span>
            <span>Following: {userData.following}</span>
            <span>Repos: {userData.public_repos}</span>
          </div>
          <a
            href={userData.html_url}
            target="_blank"
            rel="noopener noreferrer"
            className="profile-link"
          >
            View GitHub Profile
          </a>
        </div>
      )}
    </div>
  );
}

export default App;
